import React, { useState, useEffect } from "react";
import { Container, Table } from "react-bootstrap";
import { useParams } from "react-router-dom";
import axios from "axios";

// Custom Components
import CustomButton from "../../components/CustomButton.js";
import HeroSection from "../../components/HeroSection.js";

function Weather() {
  const [data, setData] = useState({
    main: {
      temp: "",
      pressure: "",
      humidity: "",
    },
    weather: [
      {
        description: "",
        main: "",
      },
    ],
  });
  const { lat, lon } = useParams({});

  useEffect(() => {
    console.log("CHANGED");
    console.log(lat);
    console.log(lon);
    const url =
      `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=` +
      process.env.REACT_APP_WEATHER_MAP_APIKEY;
    getWeatherData(url);
  }, [lat, lon]);

  const getWeatherData = (url) => {
    axios.get(url).then((response) => {
      console.log(response.data);
      setData(response.data);
    });
  };

  return (
    <HeroSection>
      <h5 className="text-white">LATITUDE: {lat}</h5>
      <h5 className="text-white">LONGITUDE: {lon}</h5>
      <Container>
        <Table striped bordered hover className="text-white px-2">
          <thead>
            <tr>
              <th>Date (mm/dd/yyyy)</th>
              <th>Temp (F)</th>
              <th className="d-none d-md-table-cell">Description</th>
              <th className="d-none d-md-table-cell">Main</th>
              <th className="d-none d-md-table-cell">Pressure</th>
              <th className="d-none d-md-table-cell">Humidity</th>
            </tr>
          </thead>
          <tbody className="text-white">
            <tr>
              <td> some date </td>
              <td> {data.main.temp} </td>
              <td className="d-none d-md-table-cell">
                {data.weather[0].description}
              </td>
              <td className="d-none d-md-table-cell">
                {data.weather[0].main}
              </td>
              <td className="d-none d-md-table-cell"> {data.main.pressure} </td>
              <td className="d-none d-md-table-cell"> {data.main.humidity} </td>
            </tr>
          </tbody>
        </Table>
      </Container>
      <CustomButton to="/home" label="Go Back Home" />
    </HeroSection>
  );
}

export default Weather;
